con:config.script:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);

def stepName =  testRunner.testCase.getPropertyValue("rotationStepName");
def testDataStep = testRunner.testCase.getTestStepByName(stepName);
def request = context.testCase.getTestStepByName(stepName);
def expectedResult = testRunner.testCase.getPropertyValue("expectedResult");

int index = 0;
for (el in context.testDataArray)  {
	testRunner.testCase.setPropertyValue('text', el);
	log.info(" Starting " + ++index + "rotation of the test case " +  testRunner.testCase.getName() + ".");

    def run = testRunner.runTestStepByName(stepName);

    def response = groovyUtils.getXmlHolder(context.testCase.getTestStepByName(stepName).name +"#Response");
    response.namespaces["sp"] = testRunner.testCase.testSuite.project.getPropertyValue("SpellServiceLink");
    def suggestedCorrection = response.getNodeValues("//sp:SpellResult[1]//sp:s").join("");

    if (expectedResult == suggestedCorrection) {
		log.info(" Test case has PASSED. Expectecd result: " + expectedResult + "; Actual result: " + suggestedCorrection + ".");
	} else {
    	     log.error(" Test case has FAILED. Expectecd result: " + expectedResult + "; Actual result: " + suggestedCorrection + ".");
    	}; 
 };

log.info("All " + index + " test cases are accomplished.");

